package lectures;

public class HelloWorld {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello world");
		int x=0;
		while(x<=300)
		{
			double c=5/9. * (x-32);
			System.out.print(x+" "+c+"\n");
			x+=20;
		}

	}

}
